import React, { useState } from 'react';
import PropTypes from 'prop-types'; 

export const Componente = (props) => {
  var [numero, setNumero] = useState(1);
  var [contador, setContador] = useState(1);

  const aumentarHandler = (e) => {
    setNumero(numero + 1)
    setContador(contador + 1);
  }

  const decrementarHandler = (e) => {
    setNumero(numero - 1);
    setContador(contador + 1);
  }

  return (
    <>
      <strong>Valor actual: {numero}</strong>
      <br/>
      <strong>Cantidad de cambios: {contador}</strong>
      <p>
        <button onClick={e => aumentarHandler(e)}>Aumentar</button>
      </p>
      <p>
        <button onClick={e => decrementarHandler(e)}>Decrementar</button>
      </p>
    </>
  )
}

export default Componente;
