import React, { useState } from 'react';

export const Componente = (props) => {
  var [yearActual, setYearActual] = useState(props.yearActual);

  const aumentarHandler = (e) => {
    setYearActual(yearActual++);
  }

  const decrementarHandler = (e) => {
    setYearActual(yearActual--);
  }

  const cambiarYearHandler = (e, year) => {
    setYearActual(year);
  }

  return (
    <>
      <strong>{yearActual}</strong>
      <p>
        <button onClick={e => aumentarHandler(e)}>+</button>
      </p>
      <p>
        <button onClick={e => decrementarHandler(e)}>-</button>
      </p>
      <p>
        <input type="number" onChange={e => cambiarYearHandler(e, e.target.value)}/>
      </p>
    </>
  )
}

export default Componente;
