export class localStorage {

  guardarEnLocalStorage(nombreItem, item) {
    //1. Conseguir los elementos.
    var elemento = JSON.parse(localStorage.getItem("peliculas"));
  
    //2. Verificar si el elemento es un Array.
    if (Array.isArray(elemento)) {
      elemento.push(item); //Agregando un elemento al Array.
    }
    else {
      elemento = [item]; //Creando el Array y agregando el primer elemento.
    }
  
    //3. Agregando pelicula al localstorage.
    localStorage.setItem("peliculas", JSON.stringify([elemento]));
  }

}
