import React from 'react';

export const EventosEnComponentes = () => {
  const click2Handler = () => {
      console.log("Evento Click Forma 2");
  }

  const click3Handler = (mensaje) => {
    console.log(mensaje);
  }

  const dobleClickHandler = () => {
    console.log("Evento Doble Click");
  }

  return (
    //El contenido HTML siempre debe estar dentro de un <div> o un Fragment.
    <>
      <hr/>
        EventosEnComponentes.js
      <hr/>
      <p>
      {/*Evento Click Forma 1 (No recomendada).*/}
        <button onClick={() => {
          console.log("Evento Click Forma 1");
        }}>Evento Click Forma 1</button>
      </p>
      <p>
        {/*Evento Click Forma 2 (Recomendada).*/}
        <button onClick={click2Handler}>Evento Click Forma 2</button>
      </p>
      <p>
        {/*Parametros en eventos.*/}
        <button onClick={e => click3Handler("Hola Mundo")}>Evento con Par√°metros</button>
      </p>
      <p>
        {/*Evento Doble Click Forma 2 (Recomendada).*/}
        <button onDoubleClick={dobleClickHandler}>Evento Doble Click</button>
      </p>
    </>
  )
}

export default EventosEnComponentes;
