import React, { useState, useEffect } from 'react'

export const Listado = (props) => {
  const [listadoState, setListadoState] = useState([]);

  const obtenerPeliculas = () => {
    var peliculas = JSON.parse(localStorage.getItem("peliculas"));
    setListadoState(peliculas);
    console.log(JSON.stringify(listadoState));
  }

  useEffect(() => {
    obtenerPeliculas();
  }, [])

  return (
    <>
      {
        listadoState.map((pelicula, indice) => { //Recorriendo el Array.
          return (
            <article className="item-pelicula">
              <li key={indice}></li>
              <h3 className="titulo">{pelicula.titulo}</h3>
              <p className="descripcion">{pelicula.descripcion}</p>
              <button className="editar">Editar</button>
              <button className="borrar">Borrar</button>
            </article>
          )
        })
      }
    </>
  )
}
