import React from 'react'

export const Editar = (props) => {
  var pelicula = props.pelicula;

  return (
    <div className="formulario-editar">
      <h3 className="titulo"></h3>
      <form action="">
        <input type="text" className="titulo-editado" name="titulo" defaultValue={pelicula.titulo}/>
        <textarea className="descripcion-editada" name="descripcion" defaultValue="{pelicula.descripcion}"></textarea>
        <input type="submit" className="editar" value="Editar"/>
      </form>
    </div>
  )
}
