import logo from './logo.svg';
import './App.css';

import { useState } from 'react';
import Listado from './components/Listado';

function App() {
  //Estados globales (Se utilizan en varios componentes).
  var [listadoState, setListadoState] = useState([]);

  return (
    <div className="contenedor">
      {/* Cabecera. */}
      <header>
        <div className="logo">
            <div className="play"></div>
        </div>
        <h1>MisPelis</h1>
      </header>

      {/* Barra de navegaci√≥n */}
      <nav>
        <ul>
          <li><a href="/#">Inicio</a></li>
          <li><a href="/#">Peliculas</a></li>
          <li><a href="/#">Blog</a></li>
          <li><a href="/#">Contacto</a></li>
        </ul>
      </nav>

      {/* Contenido principal. */}
      <section className="contenido">
        <Listado listadoState={listadoState} setListadoState={setListadoState}></Listado>
      </section>

      {/* Barra lateral. */}
      <aside className="lateral">
        <Buscador></Buscador>
        <Agregar listadoState={listadoState} setListadoState={setListadoState}></Agregar>
      </aside>

      {/* Footer. */}
      <footer>
        &copy; Master en React
      </footer>
    </div>
  );
}

export default App;
