import React, { useState, useEffect } from 'react';
import Aviso from './Aviso';

export const Componente = (props) => {
  var [numero, setNumero] = useState(0);
  var [contador, setContador] = useState(-1);

  const aumentarHandler = (e) => {
    setNumero(numero + 1)
  }

  const decrementarHandler = (e) => {
    setNumero(numero - 1);
  }

  useEffect(() => {
    setContador(contador + 1);
  }, [numero]);

  return (
    <>
      <strong className={numero < 0 ? "rojo" : numero == 0 ? "amarillo" : numero > 0 ? "verde" : ""}>Valor actual: {numero}</strong>
      <br/>
      <strong>Cantidad de cambios: {contador}</strong>
      <p>
        <button onClick={e => aumentarHandler(e)}>Aumentar</button>
      </p>
      <p>
        <button onClick={e => decrementarHandler(e)}>Decrementar</button>
      </p>
      <p>
        {(contador >= 5 && contador < 10) && <Aviso></Aviso>} {/*Cargando componente en base a condici√≥n.*/}
      </p>
    </>
  )
}

export default Componente;
