export class localStorage {

  guardarEnLocalStorage(nombreElemento, valorElemento) {
    //1. Conseguir los elementos.
    var elemento = JSON.parse(localStorage.getItem(nombreItem));
  
    //2. Verificar si el elemento es un Array.
    if (Array.isArray(elemento)) {
      elemento.push(valorItem); //Agregando un elemento al Array.
    }
    else {
      elemento = [valorItem]; //Creando el Array y agregando el primer elemento.
    }
  
    //3. Agregando pelicula al localstorage.
    localStorage.setItem(nombreItem, JSON.stringify([elemento]));
  }

}
