import React, { useState, useEffect } from 'react';

export const AjaxCapturarError = (props) => {
  var [usuarios, setUsuarios] = useState([]);
  var [cargando, setCargando] = useState(true);
  var [errores, setErrores] = useState("");

  const getUsuarios = () => {
    setTimeout(() => {
      fetch("https://reqres.malo/api/users?page=1") //Esta URL es erronea.
      .then((response) => response.json())
      .then((response) => {
        setUsuarios(response.data);
        setCargando(false);
      })
      .catch((error) => { //Capturando el error.
        setErrores(error.message);
      });
    }, 2000);
  }

  useEffect(() => {
    getUsuarios();
  }, []);

  if (cargando == true && errores == "") { //Cuando se estan cargando los datos.
    return (
      <div>
        Cargando datos...
      </div>
    )
  }
  else if (cargando == false && errores == "") { //Cuando todo funciona bien.
    return (
      <>
        <h2>Listado de usuarios "Fetch + Then"</h2>
        <ol>
          {
            usuarios.map(usuario => {
              return <li key={usuario.id}>{usuario.first_name} {usuario.last_name}</li>
            })
          }
        </ol>
      </>
    )
  }
  else if (errores != "") {
    return (
      <>
        <div>
          <p style={{color: "red"}}>
            {errores}
          </p>
        </div>
      </>
    )
  }
}

export default AjaxCapturarError;
