import React, { useState } from 'react';
import PropTypes from 'prop-types'; 

export const Componente = (props) => {
  var [yearActual, setYearActual] = useState(props.yearActual);

  const aumentarHandler = (e) => {
    setYearActual(yearActual + 1);
  }

  const decrementarHandler = (e) => {
    setYearActual(yearActual - 1);
  }

  const cambiarYearHandler = (e, year) => {
    var input = e.target.value;
    var esInputEntero = true;

    for (var i = 0; i <= input.length; i++) {
      var codigoChar = input.charCodeAt(i); //Esto retorna el "Char Code" del caracter.

      //Los valores desde 47 hasta 57 del c贸digo unicode corresponden a los caracteres 0 hasta 9.
      if (codigoChar < 48 || codigoChar > 57) {
        //Excepci贸n del caracter "-" (unicode: 45) para permitir negativos.
        if (i === 0 && codigoChar === 45) {
        }
        else {
          esInputEntero = false;
          break;
        }
      }
    }

  }

  return (
    <>
      <strong>{yearActual}</strong>
      <p>
        <button onClick={e => aumentarHandler(e)}>Aumentar</button>
      </p>
      <p>
        <button onClick={e => decrementarHandler(e)}>Decrementar</button>
      </p>
      <p>
        <input type="text" onChange={e => cambiarYearHandler(e, e.target.value)}/>
      </p>
    </>
  )
}

Componente.propTypes = { 
  yearActual: PropTypes.number.isRequired
}

export default Componente;
