import React from 'react';
import { useState } from 'react';

export const ComponenteConHookUseState = () => {
  //--------------------------------------------------------------------------------------
  //var nombre = "Daniel Alvarez";

  //Esto no funciona, si cambia el valor de la variable, per no actualiza su valor en pantalla.
  //const cambiarNombreHandler = (e, nombre) => {
    //nombre = nombre;
  //}
  //--------------------------------------------------------------------------------------

  //El hook useState permite a침adir el estado de React a un componente de funci칩n (Actualiza el valor dentro de la pantalla).

  //Declarando variable (var [variable, funcion] = funcionHook(valorVariable));.
  var [nombre, setNombre] = useState("Daniel Alvarez");

  const cambiarNombreHandler = (e, nombre) => {
    setNombre(nombre); //Cambiando el estado de la variable "nombre",
  }

  return (
    <>
      <div>
        <h3>Componente "ComponenteConHookUseState"</h3>
        <strong>{nombre}</strong>
        <button onClick={e => cambiarNombreHandler(e, "Esteban Alvarez")}>Cambiar por "Esteban Alvarez"</button>
      </div>

      <br/>

      {/*Cuando se */}
      <input type="text" onKeyDown={e => cambiarNombreHandler(e, e.target.value)} placeholder="Escribe el nuevo nombre.."/> {/*En resumen (e == Evento; target == Elemento Dom; value == Valor del elemento Dom).*/}
    </>
  )
}

export default ComponenteConHookUseState;
