import React, { useState, useEffect } from 'react';

export const AjaxCapturarError = (props) => {
  var [usuarios, setUsuarios] = useState([]);
  var [cargando, setCargando] = useState(true);

  const getUsuarios = () => {
    setTimeout(() => {
      fetch("https://reqres.malo/api/users?page=1") //1. Haciendo petición al servidor.
      .then(response => response.json()) //2. Obteniendo respuesta y transformandola a JSON.
      .then(response => { //3. Realizar acciones con la respuesta obtenida.
        setUsuarios(response.data) //4. Actualizando el estado.
        setCargando(false);
      });
    }, 2000);
  }

  useEffect(() => {
    getUsuarios();
  }, []);

  if (cargando == true) {
    return (
      <div>
        Cargando datos...
      </div>
    )
  }
  else {
    return (
      <>
        <h2>Listado de usuarios "Fetch + Then"</h2>
        <ol>
          {
            usuarios.map(usuario => {
              return <li key={usuario.id}>{usuario.first_name} {usuario.last_name}</li>
            })
          }
        </ol>
      </>
    )
  }
}

export default AjaxCapturarError;
